// Regex
var rx1Char=RegExp(".+"); // doit contenir au moins un caractère
var rxArobase=/@+/; //doit contenir au moins le caractère @
var rxCP=/^\d{5}$/; // Contient extactement 5 chiffres. \d équivaut à [0-9]
// Elements du document html
var btnControle=document.getElementById("send");
var txtSoc=document.getElementById("soc");
var txtCon=document.getElementById("con");
var txtCP=document.getElementById("cp");
var txtMail=document.getElementById("mel");
var txtVille=document.getElementById("vil");
var optChoisi=document.getElementById("defaut"); // L'option par défaut du formulaire

btnControle.addEventListener("click", function(){
    /*  
        j'ai choisi Regexp.test qui renvoie un bool plutot que String.match
         qui renvoie un Array ou null
    */
    if(!rx1Char.test(txtSoc.value)){    
        alert("Entrez le nom de la société s.v.p !");
    } 
    if(!rx1Char.test(txtCon.value)){
        alert("Entrez le nom de la personne à contacter s.v.p !");
    }
    if(!rxCP.test(txtCP.value)){
        alert('Entrez un code postal sur 5 chiffres s.v.p !');
    }
    if(!rxArobase.test(txtMail)){
        alert('Votre adresse mail doit contenir une arobase s.v.p !');
    }
});

optChoisi.addEventListener("click", function(){ // Si l'option par défaut est choisie, on flush le html
    optChoisi.innerHTML = "";
});
